import React from "react";


class Controller extends React.Component {

    constructor(props){
        super(props);

        this.state={
            start: true,
            pause: false,
            lap: false,
            reset: false
        };
    }

    startHandler(event){
        this.setState({
            ...this.state,
            start: false,
            pause: true,
            lap: true
        });
    }

    getController(){
        let output=null;
        
        if (this.state.start && !this.state.reset){
            output=(
                <div>
                    <button
                        className="btn btn-success btn-large px-5"
                        onClick={(event)=>this.startHandler()}
                    >
                        Start
                    </button>
                </div>
            );

            return output;
        }
        else if (this.state.pause && this.state.lap){
            output=(
                <div>
                    <button
                        className="btn btn-success btn-large px-5"
                        onClick={(event)=>this.startHandler()}
                    >
                        Pause
                    </button>
                    <button
                        className="btn btn-success btn-large px-5"
                        onClick={(event)=>this.startHandler()}
                    >
                        Lap
                    </button>
                </div>
            )
        }
    }

    render() {
        return(
            this.getController()
        );
    }

}

export default Controller;
