        };
    }



    render() {
        let output=null;

        if (this.state.isInput){

        }else{
            output=(
                <div className="d-flex Title">
                    <h1 className="display-4">{this.state.title}</h1>
                    <span className="ml-auto edit-icon"><i className="fas fa-pencil-alt"></i></span>
                </div>
            )
        }

        return(
            <div>{output}</div>
        );

    }
}

export default Title;
